            placeholder="Digite o ID do ingresso"
            value={texto}
            onChangeText={handleInputChange}
            style={{ borderBottomWidth: 1, marginBottom: 15, padding: 8, color: '#fff' }}
          />
          <Button title="Enviar" onPress={handleEnviar} />
        </View>
      )}

      {isConnected && (
        <View>
          <Text style={styles.metaMaskText}>Ingressos:</Text>
          <FlatList
            data={tickets}
            keyExtractor={(item, index) => index.toString()}
            renderItem={({ item }) => (
              <View style={styles.ticketItem}>
                <Text>ID do Ingresso: {item.ticketId}</Text>
                <Text>Destinatário: {item.to}</Text>
              </View>
            )}
          />
        </View>
      )}

      {isConnected && (
        <View style={styles.metaMaskText}>
          <Text style={styles.metaMaskText}>
            Preço do ingresso: {ticketPrice !== null ? `${ticketPrice} ETH` : 'Carregando...'}
          </Text>
        </View>
      )}
    </View>
  );
}

